$(document).ready(function(){$(".bxslider").bxSlider({nextSelector:"#slider-next",prevSelector:"#slider-prev",nextText:"",prevText:"",onSliderLoad:function(t){$(".slider-title").html($(".bxslider li").eq(t).attr("title")),$(".slider-text").html($(".bxslider li").eq(t).attr("alt"))},onSlideBefore:function(t,e,l){$(".slider-title").html(t.attr("title")),$(".slider-text").html(t.attr("alt"))}})});